import React, { useState } from 'react';
import axios from 'axios';

const LoginFormReact = ({ onLoginSuccess, onContinueAsGuest }) => {
  const [activeTab, setActiveTab] = useState('signin');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState(null);

  const handleTabChange = (tab) => {
    setError(null);
    setActiveTab(tab);
    // Clear inputs on tab change
    setUsername('');
    setPassword('');
    setName('');
    setEmail('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      if (activeTab === 'signin') {
        // Login request
        const response = await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/auth/token`, new URLSearchParams({
          username,
          password,
        }), {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
        });
        const token = response.data.access_token;
        localStorage.setItem('access_token', token);
        if (onLoginSuccess) onLoginSuccess(token);
      } else {
        // Registration request
        await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/auth/register`, {
          username: name,
          password,
          email,
        });
        setActiveTab('signin');
        setError('Регистрация успешна. Пожалуйста, войдите.');
      }
    } catch (err) {
      setError(err.response?.data?.detail || 'Ошибка при запросе');
    }
  };

  const styles = {
    container: {
      maxWidth: 400,
      margin: '50px auto',
      padding: 30,
      borderRadius: 10,
      textAlign: 'center',
      fontFamily: 'Arial, sans-serif',
      backgroundColor: '#2a2f4a', // dark blue background
      border: '1px solid #f2a365', // orange border
      boxShadow: '0 4px 12px rgba(242, 163, 101, 0.4)', // orange shadow
    },
    input: {
      padding: 12,
      fontSize: 16,
      borderRadius: 5,
      border: '1px solid #84a59d', // greenish border
      outline: 'none',
      transition: 'border-color 0.3s',
      backgroundColor: '#1b1f33', // darker blue background for inputs
      color: '#eaeaea', // light text
      width: '100%',
      marginBottom: 15,
    },
    button: {
      padding: 12,
      fontSize: 16,
      cursor: 'pointer',
      borderRadius: 5,
      border: 'none',
      backgroundColor: '#f2a365', // orange button
      color: '#2a2f4a', // dark blue text
      fontWeight: '600',
      transition: 'background-color 0.3s',
      width: '100%',
      marginTop: 10,
    },
    toggleButton: {
      marginTop: 15,
      background: 'none',
      border: 'none',
      color: '#84a59d', // greenish
      cursor: 'pointer',
      textDecoration: 'underline',
      fontSize: 14,
    },
    guestButton: {
      marginTop: 20,
      padding: 12,
      fontSize: 16,
      cursor: 'pointer',
      borderRadius: 5,
      border: '1px solid #f2a365', // orange border
      backgroundColor: '#2a2f4a', // dark blue background
      color: '#f2a365', // orange text
      fontWeight: '600',
      transition: 'background-color 0.3s',
      width: '100%',
    },
    error: {
      marginTop: 10,
      color: '#ff6b6b', // softer red
      fontWeight: '600',
    },
    tabs: {
      maxHeight: 50,
      height: 50,
      display: 'flex',
      backgroundColor: '#FFF',
      marginTop: 20,
    },
    tab: {
      flex: 1,
      color: '#5D708A',
      textAlign: 'center',
      lineHeight: '50px',
      transition: 'all 0.2s',
      cursor: 'pointer',
    },
    activeTab: {
      color: '#3F4C7F',
      fontWeight: 'bold',
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.tabs}>
        <div
          style={activeTab === 'signin' ? { ...styles.tab, ...styles.activeTab } : styles.tab}
          onClick={() => handleTabChange('signin')}
        >
          Sign In
        </div>
        <div
          style={activeTab === 'register' ? { ...styles.tab, ...styles.activeTab } : styles.tab}
          onClick={() => handleTabChange('register')}
        >
          Register
        </div>
      </div>
      <form onSubmit={handleSubmit}>
        {activeTab === 'signin' && (
          <>
            <input
              style={styles.input}
              type="text"
              placeholder="USERNAME"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              autoComplete="username"
            />
            <input
              style={styles.input}
              type="password"
              placeholder="PASSWORD"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              autoComplete="current-password"
            />
            <input style={styles.button} type="submit" value="ENTER" />
          </>
        )}
        {activeTab === 'register' && (
          <>
            <input
              style={styles.input}
              type="text"
              placeholder="NAME"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              autoComplete="name"
            />
            <input
              style={styles.input}
              type="email"
              placeholder="EMAIL"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              autoComplete="email"
            />
            <input style={styles.button} type="submit" value="SIGN ME UP!" />
          </>
        )}
      </form>
      {error && <div style={styles.error}>{error}</div>}
      <button style={styles.toggleButton} onClick={() => handleTabChange(activeTab === 'signin' ? 'register' : 'signin')}>
        {activeTab === 'signin' ? 'Создать аккаунт' : 'Уже есть аккаунт? Войти'}
      </button>
      <button style={styles.guestButton} onClick={onContinueAsGuest} type="button">
        Продолжить без авторизации
      </button>
    </div>
  );
};

export default LoginFormReact;
