import React from 'react';
import { useNavigate } from 'react-router-dom';

const TestAuth = () => {
  const navigate = useNavigate();

  const handleTestRedirect = () => {
    // This will test if we can access the users page without authentication
    navigate('/users');
  };

  return (
    <div style={{ padding: '20px', color: '#eaeaea' }}>
      <h2>Тест аутентификации</h2>
      <button 
        onClick={handleTestRedirect}
        style={{ 
          padding: '10px 20px', 
          backgroundColor: '#f2a365', 
          color: '#2a2f4a', 
          border: 'none', 
          borderRadius: '5px',
          cursor: 'pointer',
          fontWeight: 'bold'
        }}
      >
        Попробовать перейти к /users без авторизации
      </button>
      <p style={{ marginTop: '20px' }}>
        Если защита работает правильно, вы будете перенаправлены на главную страницу.
      </p>
    </div>
  );
};

export default TestAuth;
