import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ErrorsList({ token, onLogout }) {
  const [errors, setErrors] = useState([]);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchErrors = async () => {
      try {
        const response = await axios.get('/api/errors/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setErrors(response.data);
      } catch (err) {
        if (err.response && err.response.status === 401) {
          onLogout();
        } else {
          setError('Ошибка при загрузке ошибок');
        }
      }
    };
    fetchErrors();
  }, [token, onLogout]);

  const filteredErrors = errors.filter((err) =>
    err.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h2>Список ошибок</h2>
      <input
        type="text"
        placeholder="Поиск ошибок..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: '10px', padding: '5px', width: '100%' }}
      />
      {error && <p style={{color: 'red'}}>{error}</p>}
      <ul>
        {filteredErrors.map((err) => (
          <li key={err.id}>{err.name}</li>
        ))}
      </ul>
      <button onClick={onLogout}>Выйти</button>
    </div>
  );
}

export default ErrorsList;
