import React, { useEffect, useState } from 'react';
import axios from 'axios';

const apiClient = axios.create({
  baseURL: 'http://localhost:8000',
});

import { Typography, Button, TextField, Dialog, DialogActions, DialogContent, DialogTitle, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';

function AdminPanelContactInfo({ token }) {
  const [contacts, setContacts] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [editContact, setEditContact] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [name, setName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    const fetchContacts = async () => {
      setLoading(true);
      try {
        const response = await apiClient.get('/api/contact_info/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setContacts(response.data);
        setError('');
      } catch (err) {
        setError('Ошибка при загрузке контактов');
      } finally {
        setLoading(false);
      }
    };

    fetchContacts();
  }, [token]);

  const handleEditClick = (contact) => {
    setEditContact(contact);
    setName(contact.name);
    setPhoneNumber(contact.phone_number);
    setDescription(contact.description || '');
    setOpenDialog(true);
  };

  const handleDeleteClick = async (contactId) => {
    if (!window.confirm('Вы уверены, что хотите удалить контакт?')) return;
    try {
      await axios.delete(`/api/contact_info/${contactId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setContacts(contacts.filter((c) => c.id !== contactId));
    } catch (err) {
      setError('Ошибка при удалении контакта');
    }
  };

  const handleDialogClose = () => {
    setOpenDialog(false);
    setEditContact(null);
    setName('');
    setPhoneNumber('');
    setDescription('');
  };

  const handleDialogSave = async () => {
    try {
      if (editContact) {
        await axios.put(`/api/contact_info/${editContact.id}`, {
          name,
          phone_number: phoneNumber,
          description,
        }, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setContacts(contacts.map((c) => (c.id === editContact.id ? { ...c, name, phone_number: phoneNumber, description } : c)));
      } else {
        await axios.post('/api/contact_info/', {
          name,
          phone_number: phoneNumber,
          description,
        }, {
          headers: { Authorization: `Bearer ${token}` },
        });
        // Refresh contacts list
        const response = await axios.get('/api/contact_info/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setContacts(response.data);
      }
      handleDialogClose();
    } catch (err) {
      setError('Ошибка при сохранении контакта');
    }
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom sx={{ color: '#f2a365' }}>
        Контактная информация
      </Typography>
      {error && (
        <Typography sx={{ color: '#ff6b6b', fontWeight: '700' }}>
          {error}
        </Typography>
      )}
      <Button
        variant="contained"
        onClick={() => setOpenDialog(true)}
        sx={{
          mb: 2,
          backgroundColor: '#f2a365',
          color: '#2a2f4a',
          '&:hover': { backgroundColor: '#d18c4a' },
          fontWeight: '700',
        }}
      >
        Добавить контакт
      </Button>
      <TableContainer
        component={Paper}
        sx={{ backgroundColor: '#2a2f4a', color: '#eaeaea' }}
      >
        <Table aria-label="contacts table" sx={{ color: '#eaeaea' }}>
          <TableHead>
            <TableRow>
              <TableCell sx={{ color: '#f2a365' }}>Название</TableCell>
              <TableCell sx={{ color: '#f2a365' }}>Телефон</TableCell>
              <TableCell sx={{ color: '#f2a365' }}>Описание</TableCell>
              <TableCell sx={{ color: '#f2a365' }}>Действия</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {contacts.map((contact) => (
              <TableRow key={contact.id} sx={{ color: '#eaeaea' }}>
                <TableCell>{contact.name}</TableCell>
                <TableCell>{contact.phone_number}</TableCell>
                <TableCell>{contact.description}</TableCell>
                <TableCell>
                  <Button
                    variant="outlined"
                    onClick={() => handleEditClick(contact)}
                    sx={{
                      mr: 1,
                      borderColor: '#f2a365',
                      color: '#f2a365',
                      '&:hover': {
                        backgroundColor: '#f2a365',
                        color: '#2a2f4a',
                      },
                    }}
                  >
                    Редактировать
                  </Button>
                  <Button
                    variant="outlined"
                    onClick={() => handleDeleteClick(contact.id)}
                    sx={{
                      borderColor: '#f2a365',
                      color: '#f2a365',
                      '&:hover': {
                        backgroundColor: '#f2a365',
                        color: '#2a2f4a',
                      },
                    }}
                  >
                    Удалить
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Dialog open={openDialog} onClose={handleDialogClose}>
        <DialogTitle sx={{ color: '#f2a365' }}>
          {editContact ? 'Редактировать контакт' : 'Добавить контакт'}
        </DialogTitle>
        <DialogContent sx={{ backgroundColor: '#2a2f4a' }}>
          <TextField
            autoFocus
            margin="dense"
            label="Название"
            fullWidth
            value={name}
            onChange={(e) => setName(e.target.value)}
            sx={{
              '& label': { color: '#f2a365' },
              '& label.Mui-focused': { color: '#f2a365' },
              '& .MuiInputBase-root': {
                color: '#eaeaea',
                backgroundColor: '#1b1f33',
                borderRadius: 1,
              },
              '& .MuiOutlinedInput-notchedOutline': {
                borderColor: '#84a59d',
              },
              '& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline': {
                borderColor: '#f2a365',
              },
            }}
          />
          <TextField
            margin="dense"
            label="Телефон"
            fullWidth
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            sx={{
              '& label': { color: '#f2a365' },
              '& label.Mui-focused': { color: '#f2a365' },
              '& .MuiInputBase-root': {
                color: '#eaeaea',
                backgroundColor: '#1b1f33',
                borderRadius: 1,
              },
              '& .MuiOutlinedInput-notchedOutline': {
                borderColor: '#84a59d',
              },
              '& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline': {
                borderColor: '#f2a365',
              },
            }}
          />
          <TextField
            margin="dense"
            label="Описание"
            fullWidth
            multiline
            minRows={2}
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            sx={{
              '& label': { color: '#f2a365' },
              '& label.Mui-focused': { color: '#f2a365' },
              '& .MuiInputBase-root': {
                color: '#eaeaea',
                backgroundColor: '#1b1f33',
                borderRadius: 1,
              },
              '& .MuiOutlinedInput-notchedOutline': {
                borderColor: '#84a59d',
              },
              '& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline': {
                borderColor: '#f2a365',
              },
            }}
          />
        </DialogContent>
        <DialogActions sx={{ backgroundColor: '#2a2f4a' }}>
          <Button sx={{ color: '#f2a365' }} onClick={handleDialogClose}>
            Отмена
          </Button>
          <Button
            onClick={handleDialogSave}
            sx={{
              backgroundColor: '#f2a365',
              color: '#2a2f4a',
              '&:hover': { backgroundColor: '#d18c4a' },
              fontWeight: '700',
            }}
          >
            Сохранить
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default AdminPanelContactInfo;
