function CreateUserModal({ open, onClose, onSave, roles, token }) {
  const [newUser, setNewUser] = useState({
    username: '',
    password: '',
    realname: '',
    role_id: null,  // Initialize as null to match backend logic
  });
  const [error, setError] = useState('');

  const handleChange = (field, value) => {
    setNewUser((prev) => ({ ...prev, [field]: value }));
  };

  const handleSubmit = async () => {
    if (!newUser.username || !newUser.password || !newUser.realname) {
      setError('Имя пользователя, пароль и настоящее имя обязательны');
      return;
    }

    try {
      await onSave({
        ...newUser,
        role_id: newUser.role_id || null,  // Explicitly send null if no role selected
      });
      onClose();
      setNewUser({
        username: '',
        password: '',
        realname: '',
        role_id: null,
      });
      setError('');
    } catch (err) {
      setError(err.message || 'Ошибка при создании пользователя');
    }
  };

  return (
    <Modal open={open} onClose={onClose}>
      <Box sx={style}>
        <Typography variant="h6" sx={{ color: '#f2a365', mb: 2 }}>
          Создать нового пользователя
        </Typography>
        
        {error && (
          <Typography sx={{ color: '#ff6b6b', mb: 2 }}>
            {error}
          </Typography>
        )}

        <TextField
          label="Имя пользователя*"
          fullWidth
          value={newUser.username}
          onChange={(e) => handleChange('username', e.target.value)}
          sx={{ mb: 2 }}
          // ... rest of your TextField props
        />

        <TextField
          label="Пароль*"
          type="password"
          fullWidth
          value={newUser.password}
          onChange={(e) => handleChange('password', e.target.value)}
          sx={{ mb: 2 }}
          // ... rest of your TextField props
        />

        <TextField
          label="Настоящее имя*"
          fullWidth
          value={newUser.realname}
          onChange={(e) => handleChange('realname', e.target.value)}
          sx={{ mb: 2 }}
          // ... rest of your TextField props
        />

        <Select
          value={newUser.role_id || ''}
          onChange={(e) => handleChange('role_id', e.target.value || null)}
          fullWidth
          sx={{ mb: 2 }}
          displayEmpty
        >
          <MenuItem value="">Не указана (будет назначена роль "Пользователь")</MenuItem>
          {roles.map((role) => (
            <MenuItem key={role.id} value={role.id}>
              {role.name}
            </MenuItem>
          ))}
        </Select>

        {/* ... rest of your modal code */}
      </Box>
    </Modal>
  );
}
