import React, { useState } from "react";
import axios from "axios";

function AuthPage({ onLoginSuccess, onContinueAsGuest }) {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const toggleMode = () => {
    setError(null);
    setIsLogin(!isLogin);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      if (isLogin) {
        // Login request
        const response = await axios.post("http://localhost:8000/api/auth/token", new URLSearchParams({
          username,
          password,
        }), {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
        });
        const token = response.data.access_token;
        localStorage.setItem("access_token", token);
        onLoginSuccess(token);
      } else {
        // Registration request
        await axios.post("http://localhost:8000/api/auth/register", {
          username,
          password,
        });
        setIsLogin(true);
        setError("Регистрация успешна. Пожалуйста, войдите.");
      }
    } catch (err) {
      setError(err.response?.data?.detail || "Ошибка при запросе");
    }
  };

  return (
    <div style={styles.container}>
      <h2>{isLogin ? "Вход" : "Регистрация"}</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          placeholder="Имя пользователя"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          style={styles.input}
          autoComplete="username"
        />
        <input
          type="password"
          placeholder="Пароль"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={styles.input}
          autoComplete={isLogin ? "current-password" : "new-password"}
        />
        <button type="submit" style={styles.button}>
          {isLogin ? "Войти" : "Зарегистрироваться"}
        </button>
      </form>
      {error && <div style={styles.error}>{error}</div>}
      <button onClick={toggleMode} style={styles.toggleButton}>
        {isLogin ? "Создать аккаунт" : "Уже есть аккаунт? Войти"}
      </button>
      <button onClick={onContinueAsGuest} style={styles.guestButton}>
        Продолжить без авторизации
      </button>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: 400,
    margin: "50px auto",
    padding: 20,
    borderRadius: 8,
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
    backgroundColor: "rgba(128, 128, 128, 0.5)", // прозрачный серый фон
    border: "1px solid #ccc",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: 10,
    marginBottom: 10,
  },
  input: {
    padding: 10,
    fontSize: 16,
  },
  button: {
    padding: 10,
    fontSize: 16,
    cursor: "pointer",
  },
  toggleButton: {
    marginTop: 10,
    background: "none",
    border: "none",
    color: "blue",
    cursor: "pointer",
    textDecoration: "underline",
  },
  guestButton: {
    marginTop: 20,
    padding: 10,
    fontSize: 16,
    cursor: "pointer",
  },
  "@keyframes gradientShift": {
    "0%": {
      backgroundPosition: "0% 50%",
    },
    "50%": {
      backgroundPosition: "100% 50%",
    },
    "100%": {
      backgroundPosition: "0% 50%",
    },
  },
};

export default AuthPage;
