import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ErrorsList({ token, onLogout }) {
  const [errors, setErrors] = useState([]);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [expandedErrorIds, setExpandedErrorIds] = useState(new Set());

  useEffect(() => {
    const fetchErrors = async () => {
      try {
        const response = await axios.get('/api/errors/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setErrors(response.data);
      } catch (err) {
        if (err.response && err.response.status === 401) {
          onLogout();
        } else {
          setError('Ошибка при загрузке ошибок');
        }
      }
    };
    fetchErrors();
  }, [token, onLogout]);

  const filteredErrors = errors.filter((err) =>
    err.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const toggleExpand = (id) => {
    setExpandedErrorIds((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(id)) {
        newSet.delete(id);
      } else {
        newSet.add(id);
      }
      return newSet;
    });
  };

  return (
    <div>
      <h2>Список ошибок</h2>
      <input
        type="text"
        placeholder="Поиск ошибок..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: '10px', padding: '5px', width: '100%' }}
      />
      {error && <p style={{color: 'red'}}>{error}</p>}
      <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
        {filteredErrors.map((err) => (
          <li key={err.id} style={{ border: '1px solid #ccc', marginBottom: '5px', borderRadius: '4px' }}>
            <div
              onClick={() => toggleExpand(err.id)}
              style={{ cursor: 'pointer', padding: '10px', backgroundColor: '#f0f0f0' }}
            >
              {err.name}
            </div>
            {expandedErrorIds.has(err.id) && (
              <div style={{ padding: '10px' }}>
                <p>{err.description || 'Описание отсутствует'}</p>
                {err.images && err.images.length > 0 && (
                  <div>
                    {err.images.map((img, idx) => (
                      <img key={idx} src={img.url} alt={`Error ${err.name} image ${idx + 1}`} style={{ maxWidth: '100%', marginBottom: '10px' }} />
                    ))}
                  </div>
                )}
              </div>
            )}
          </li>
        ))}
      </ul>
      <button onClick={onLogout}>Выйти</button>
    </div>
  );
}

export default ErrorsList;
