import React, { useState } from 'react';
import axios from 'axios';
import './styles/Loginform.scss';

const LoginFormReact = ({ onLoginSuccess, onContinueAsGuest }) => {
  const [activeTab, setActiveTab] = useState('signin');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState(null);

  const handleTabChange = (tab) => {
    setError(null);
    setActiveTab(tab);
    // Clear inputs on tab change
    setUsername('');
    setPassword('');
    setName('');
    setEmail('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      if (activeTab === 'signin') {
        // Login request
        const response = await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/auth/token`, new URLSearchParams({
          username,
          password,
        }), {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
        });
        const token = response.data.access_token;
        localStorage.setItem('access_token', token);
        if (onLoginSuccess) onLoginSuccess(token);
      } else {
        // Registration request
        await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/auth/register`, {
          username: name,
          password,
          email,
        });
        setActiveTab('signin');
        setError('Регистрация успешна. Пожалуйста, войдите.');
      }
    } catch (err) {
      setError(err.response?.data?.detail || 'Ошибка при запросе');
    }
  };

  const styles = {
    container: {
      maxWidth: 400,
      margin: '50px auto',
      padding: 30,
      borderRadius: 10,
      textAlign: 'center',
      fontFamily: 'Arial, sans-serif',
      background: 'linear-gradient(135deg, #6FAAFF 0%, #955DFF 100%)', // new gradient
      border: '1px solid #f2a365', // orange border
      boxShadow: '0 4px 12px rgba(242, 163, 101, 0.4)', // orange shadow
    },
    input: {
      padding: 12,
      fontSize: 16,
      borderRadius: 5,
      border: '1px solid #84a59d', // greenish border
      outline: 'none',
      transition: 'border-color 0.3s',
      backgroundColor: '#f6f7f9', // lighter background for inputs
      color: '#000', // dark text
      width: '100%',
      marginBottom: 15,
    },
    button: {
      backgroundColor: '#f2a365', // orange button
      color: '#2a2f4a', // dark blue text
      padding: '12px',
      fontSize: '16px',
      cursor: 'pointer',
      borderRadius: '5px',
      border: 'none',
      fontWeight: '600',
      transition: 'background-color 0.3s',
      width: '100%',
      marginTop: '10px',
    },
    guestButton: {
      marginTop: '20px',
      padding: '12px',
      fontSize: '16px',
      cursor: 'pointer',
      borderRadius: '5px',
      border: '1px solid #f2a365', // orange border
      backgroundColor: '#2a2f4a', // dark blue background
      color: '#f2a365', // orange text
      fontWeight: '600',
      transition: 'background-color 0.3s',
      width: '100%',
    },
    error: {
      marginTop: '10px',
      color: '#ff6b6b', // softer red
      fontWeight: '600',
    },
    tabActive: {
      color: '#3F4C7F',
      fontWeight: 'bold',
    },
  };

  return (
    <div style={styles.container}>
      <input
        type="radio"
        name="tab"
        id="signin"
        checked={activeTab === 'signin'}
        onChange={() => handleTabChange('signin')}
      />
      <input
        type="radio"
        name="tab"
        id="register"
        checked={activeTab === 'register'}
        onChange={() => handleTabChange('register')}
      />
      <form onSubmit={handleSubmit} className="pages">
        <div className="page">
          <div className="input">
            <input
              className="text"
              type="text"
              placeholder="USERNAME"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              autoComplete="username"
              style={styles.input}
            />
          </div>
          <div className="input">
            <input
              className="text"
              type="password"
              placeholder="PASSWORD"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              required
              autoComplete="name"
            />
          </div>
          <div className="input">
            <input
              className="text"
              type="email"
              placeholder="EMAIL"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              autoComplete="email"
            />
          </div>
          <div className="input">
            <input type="submit" value="SIGN ME UP!" style={styles.button} />
          </div>
        </div>
      </form>
      {error && <div className="error" style={styles.error}>{error}</div>}
      <div className="tabs">
        <label
          htmlFor="signin"
          className="tab"
          onClick={() => handleTabChange('signin')}
          style={activeTab === 'signin' ? styles.tabActive : {}}
        >
          <div className="text">Sign In</div>
        </label>
        <label
          htmlFor="register"
          className="tab"
          onClick={() => handleTabChange('register')}
          style={activeTab === 'register' ? styles.tabActive : {}}
        >
          <div className="text">Register</div>
        </label>
      </div>
      <button onClick={() => handleTabChange(activeTab === 'signin' ? 'register' : 'signin')} style={styles.toggleButton}>
        {activeTab === 'signin' ? 'Создать аккаунт' : 'Уже есть аккаунт? Войти'}
      </button>
      <button onClick={onContinueAsGuest} className="guestButton" type="button" style={styles.guestButton}>
        Продолжить без авторизации
      </button>
    </div>
  );
};

export default LoginFormReact;
